mapper.select:
/*基本信息*/ SELECT id, vip_name vipName,vip_sex vipSex,vip_phone vipPhone,vip_value_name vipValueName,vip_value_id vipValueId,business_id businessId, vip_birthday vipBirthday,vip_birthday_nl vipBirthdayNl,hideFlag,is_leap isLeap, anniversary_date anniversaryDate,anniversary_title title,calendar_type calendarType,nexttime, surplusTime,type FROM ( SELECT id, business_id, vip_name, vip_sex, vip_phone, vip_value_name, vip_value_id, vip_birthday, vip_birthday_nl, birth_flag as calendar_type, '' AS anniversary_date, CONCAT( vip_name, IF ( vip_sex = '女', ' 小姐', ' 先生' ), '的生日' ) AS anniversary_title, hide_birthday_year as hideFlag, is_leap, next_vip_birthday AS nexttime, /*剩余天数*/ datediff(next_vip_birthday,SYSDATE()) as surplusTime, '1' AS type FROM vip WHERE business_id = #{businessId} and vip_phone is not null /*客户存在的条件*/ and vip_birthday is not null and vip_birthday != '' /*客户价值条件*/
AND vip_value_id = #{customerValueId}
AND vip_name like concat('%',#{name},'%')
UNION ALL SELECT b.id AS id, a.business_id, b.vip_name, b.vip_sex, b.vip_phone, b.vip_value_name, b.vip_value_id, '' AS vip_birthday, '' AS vip_birthday_nl, a.calendar_type AS calendar_type, a.anniversary_date, a.anniversary_title, anniversary_year_flag as hideFlag, a.is_leap, a.next_anniversary_time AS nexttime, datediff(next_anniversary_time, SYSDATE()) as surplusTime, /*剩余天数*/ '0' AS type FROM (select * from anniversary c where business_id = #{businessId} and EXISTS( SELECT id FROM vip o WHERE o.id=c.vip_id and business_id = #{businessId})) a LEFT JOIN (select * from vip where business_id = #{businessId} and vip_phone is not null
AND vip_value_id = #{customerValueId}
AND vip_name like concat('%',#{name},'%') ) b ON a.vip_id = b.id ) c where surplusTime >= 0
AND surplusTime <= #{surplusTime}
