mapper.select:
select * from (
  select vip_id,count(1) as expenseTime  from (
      select  o.vip_id from resv_order o
      where  business_id=#{data.businessId} and  status in (2,3)  and o.resv_date >= #{startDate} and o.resv_date < #{endDate}
       and o.vip_id is not null
      group by batch_no
      ) t group by  vip_id
  )countc
  where
   1=1
   and countc.expenseTime >= #{data.expenseTimeStart} 
     and countc.expenseTime <  #{data.expenseTimeEnd}

select * from (
  select vip_id,sum(expenseMoney) as expenseMoney  from (
    select  sum(o.payamount) as expenseMoney,o.vip_id  from resv_order o
        where business_id=#{data.businessId} and  status in (2,3)  and o.resv_date >= #{startDate} and o.resv_date < #{endDate}
         and o.vip_id is not null
        group by batch_no
  ) t group by  t.vip_id
  ) sumc
  where 1=1
     and sumc.expenseMoney >= #{data.expenseMoneyStart} 
       and sumc.expenseMoney <  #{data.expenseMoneyEnd}
