mapper.select:
select id,batch_no,business_id,third_order_no,vip_name,vip_phone,vip_sex,table_type,table_type_name, remark,resv_num,status,flag,source,meal_type_id,meal_type_name,resv_source,resv_date,dest_date, created_at, updated_at,order_result from (SELECT id,batch_no,business_id,third_order_no,vip_name,vip_phone,vip_sex,table_type,table_type_name, remark,resv_num,status,flag,source,meal_type_id,meal_type_name,source AS resv_source, DATE_FORMAT(resv_date,"%Y-%m-%d") resv_date, DATE_FORMAT(resv_date,"%H:%i") dest_date, created_at, updated_at, CASE WHEN r.status = '10' THEN '未处理' WHEN r.status = '20' OR r.status = '70' OR r.status = '80' THEN '客户取消' WHEN r.status = '30' THEN '已拒单' WHEN r.status = '40' OR r.status = '50' OR r.status = '60' THEN '已接单' WHEN r.status = '45' THEN '已接单未选座' END order_result FROM resv_order_third r ) A WHERE A.business_id = #{businessId}
AND A.source = #{source}
AND A.order_result = #{orderResult}
AND SUBSTR(A.created_at,1,10) = #{createtime}
ORDER BY A.created_at DESC
