mapper.select:
select count(vip_id) from ( select count(o.batch_no) order_num, vip_id from resv_sys.resv_order_android o where o.business_id = #{businessId}
and o.status=#{status}
group by o.vip_id ) t where order_num > <
#{orderTime}

select count(vip_id) from ( select max(resv_date) as resv_date, vip_id from resv_sys.resv_order_android o where o.business_id = #{businessId}
group by o.vip_id ) t
where resv_date > < #{orderDate}

select v.id vipId,v.vip_name vipName,v.vip_phone vipPhone,v.vip_value_id vipValueId,v.vip_value_name vipValueName, vs.resv_batch_count resvBatchCount,vs.resv_order_count resvOrderCount,vs.resv_people_num resvPeopleNum, vs.meal_batch_count mealBatchCount,vs.meal_order_count mealOrderCount,vs.meal_people_num mealPeopleNum,vs.last_meal_date lastMealDate from vip v left join vip_statistics vs on v.id = vs.vip_id
v.business_id=#{businessId}
and (v.vip_name like concat('%',#{queryVal},'%') or v.vip_phone like concat('%',#{queryVal},'%'))

SELECT
E.id as id,E.vip_name as vipName ,E.vip_sex as vipSex,E.vip_phone as vipPhone,E.vip_value_name as
vipValueName,E.vip_class_name as vipClassName,
E.actResvTimes as actResvTimes,E.paySum,
DATE_FORMAT( E.eattime, '%Y-%m-%d' ) AS lastEatTime,vip_company as vipCompany,vip_postion as
vipPostion,created_at as createdAt
FROM
(
SELECT
C.id,C.vip_name,C.vip_sex,C.vip_phone,C.vip_value_name,C.vip_class_name,C.actResvTimes,C.paySum,
D.eattime,vip_company,vip_postion,created_at
FROM
(
SELECT
A.id,A.vip_name,A.vip_sex,A.vip_phone,A.vip_value_name,A.vip_class_name,
ifnull( B.times , 0 ) AS actResvTimes,B.paySum,vip_company,vip_postion,created_at
FROM
( SELECT id,vip_name,vip_sex,vip_phone,vip_value_name,vip_class_name,vip_company,vip_postion,created_at FROM vip
WHERE
business_id = #{businessId}
/*加客户普通查询条件*/

    AND vip_name like CONCAT('%',#{vipName},'%')


    AND vip_phone like CONCAT('%',#{vipPhone},'%')


    AND vip_value_id = #{vipValueId}


    AND vip_class_name = #{vipClassId}

) A
LEFT JOIN ( SELECT ifnull( COUNT( DISTINCT batch_no ), 0 ) AS times,ifnull( SUM(payamount), 0 ) AS paySum,
vip_id FROM resv_order_android where status = 3 AND business_id = #{businessId} GROUP BY vip_id ) B ON A.id = B.vip_id
) C
LEFT JOIN ( SELECT Max( updated_at ) eattime, vip_id FROM resv_order_android where status = 3 AND business_id =
#{businessId} GROUP BY vip_id ) D ON C.id = D.vip_id
) E
WHERE
1=1
/*加计算所得条件*/
/*实际就餐次数几次以上*/

    AND actResvTimes >= #{actResvTimes}

/*实际就餐次数几次以内*/

    AND actResvTimes <= #{actResvTimesLt}

/*就餐日期多少天以内*/

    AND eattime >= #{lastEatTime}

/*就餐日期多少天以外*/

    AND eattime <= #{lastEatTimeLt}

/*支付金额多少以上*/

    AND paySum >= #{paySum}

/*支付金额多少以内*/

    AND paySum <= #{paySumLt}

mapper.update:
update vip
set vip_class_id = null,
    vip_class_name = ""
where
    business_id = #{businessId}
    and vip_class_id = #{id}

mapper.select:
SELECT E.id as id,E.vip_name as vipName ,E.vip_sex as vipSex,E.vip_phone as vipPhone, E.vip_value_name as vipValueName,E.vip_class_name as vipClassName, E.actResvTimes as actResvTimes, DATE_FORMAT( E.eattime, '%Y-%m-%d' ) AS lastEatTime, E.vip_company as vipCompany,E.vip_postion as vipPostion FROM ( SELECT C.id,C.vip_name,C.vip_sex,C.vip_phone,C.vip_value_name,C.vip_class_name,C.actResvTimes, D.eattime,C.vip_company,C.vip_postion FROM ( SELECT A.id,A.vip_name,A.vip_sex,A.vip_phone,A.vip_value_name,A.vip_class_name, ifnull( B.times, 0 ) AS actResvTimes,A.vip_company ,A.vip_postion FROM ( SELECT id,vip_name,vip_sex,vip_phone,vip_value_name,vip_class_name,vip_company ,vip_postion FROM vip WHERE business_id = #{businessId} /*加客户普通查询条件*/
AND vip_name like CONCAT('%',#{vipName},'%')
AND vip_phone like CONCAT('%',#{vipPhone},'%')
AND vip_value_id = #{vipValueId}
AND vip_class_name = #{vipClassId}
) A LEFT JOIN ( SELECT ifnull( COUNT( DISTINCT batch_no ), 0 ) AS times, vip_id FROM resv_order_android where status = 3 AND business_id = #{businessId} GROUP BY vip_id ) B ON A.id = B.vip_id ) C LEFT JOIN ( SELECT Max( updated_at ) eattime, vip_id FROM resv_order_android where status = 3 AND business_id = #{businessId} GROUP BY vip_id ) D ON C.id = D.vip_id ) E WHERE 1=1 /*加计算所得条件*/
AND actResvTimes >= #{actResvTimes}
AND eattime >= #{lastEatTime}
Order by lastEatTime

SELECT A.id,A.vip_name as vipName,A.vip_sex as vipSex,A.vip_phone as vipPhone,A.vip_value_name as vipValueName,A.vip_class_name as vipClassName, DATE_FORMAT( B.eattime, '%Y-%m-%d' ) AS lastEatTime , A.vip_company as vipCompany,A.vip_postion as vipPostion FROM (SELECT id,vip_name,vip_sex,vip_phone,vip_value_name,vip_class_name,vip_company ,vip_postion FROM vip WHERE business_id = #{businessId}
AND (vip_name like CONCAT('%',#{keyword},'%') or vip_phone like CONCAT('%',#{keyword},'%'))
AND vip_value_name = #{vipValueName}
ORDER BY created_at ) A LEFT JOIN ( SELECT Max( updated_at ) eattime, vip_id FROM resv_order_android where status = 3 AND business_id = #{businessId} GROUP BY vip_id ) B ON A.id = B.vip_id

select *
from vip
where id in

    #{item}

and app_user_id = #{appUserId}

select * from vip where id in
#{item}
and (app_user_id is null or app_user_id = 0)
